#!/usr/bin/bashNUM_1=$1NUM_2=$2TEST_1=falseTEST_2=falseif [[ $NUM_1 =~ ^[+-]?[0-9]+$ ]]; thenTEST_1=trueelif [[ $NUM_1 =~ ^[+-]?[0-9]+\.?[0-9]*$ ]]; thenTEST_1=trueelseecho 'Первый аргумент не является числом'exit 1fiif [ -z $NUM_2 ]; thenecho 'Второй аргумент пуст'exit 1elif [[ $NUM_2 =~ ^[+-]?[0-9]+$ ]]; thenTEST_2=trueelif [[ $NUM_2 =~ ^[+-]?[0-9]+\.?[0-9]*$ ]]; thenTEST_2=trueelseecho 'Второй аргумент не является числом'exit 1fiif [[ TEST_1 && TEST_2 ]]; thenawk "BEGIN {print $NUM_1 + $NUM_2}"fi